catalog_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }
    //Type appropriate comment here, and begin script below

    var reqFor = g_form.getValue('requested_for');

    //g_form.addInfoMessage('[SelectMembers][getTeamMembers] Start CS! ' + reqFor);

	g_form.clearValue('v_members');

    var teamMembers = new GlideAjax('AjaxServiceCatalogUtils');
    //teamMembers.addParam(sys_parm)
    teamMembers.addParam('sysparm_name', 'getGroupMembers');
    teamMembers.addParam('sysparm_groupSysID', newValue);

    //g_form.addInfoMessage('[SelectMembers][getTeamMembers] before getAnswer CS! ' + newValue);

    teamMembers.getXML(function(response) {
        //g_form.addInfoMessage('[SelectMembers][getTeamMembers] after getAnswer CS!');
        var members = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));
		members.push(reqFor);

        if (members != '') {
            //g_form.addInfoMessage('[SelectMembers][getTeamMembers] members: ' + members);
            g_form.setValue('v_members', members.join(','));
        }

    });

}
