function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue == '') {
        return;
    }

	var defChef = [
		{
			"w_email": "fred.luddy@example.com",
			"w_nachname": "Luddy",
			"w_vorsitzender": "5137153cc611227c000bbd1bd8cd2005",
			"w_vorname": "Fred"
		}
	];

	//g_form.addInfoMessage('Chefs: ' + JSON.stringify(defChef));

    var chefsValue = g_form.getValue('v_chefs');
    //g_form.addInfoMessage('Chefs: ' + chefsValue);

    // Ergebnis:
    // Chefs: [{"w_email":"aileen.mottern@example.com","w_nachname":"Mottern","w_vorsitzender":"71826bf03710200044e0bfc8bcbe5d3b","w_vorname":"Aileen"}] 

    //Type appropriate comment here, and begin script below
	g_form.clearValue('v_chefs');
	//g_form.setValue('v_chefs', JSON.stringify(defChef));

    var teamMembers = new GlideAjax('AjaxServiceCatalogUtils');
    //teamMembers.addParam(sys_parm)
    teamMembers.addParam('sysparm_name', 'getChefsfromTeam');
    teamMembers.addParam('sysparm_groupSysID', newValue);

    //g_form.addInfoMessage('[SelectMembers][PreFill Chefs] before getAnswer CS! ' + newValue);

    teamMembers.getXML(function(response) {
        //g_form.addInfoMessage('[SelectMembers][PreFill Chefs] after getAnswer CS!');
        var members = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));

        if (members != '') {
			//g_form('Members');
            //g_form.addInfoMessage('[SelectMembers][PreFill Chefs] members: ' + JSON.stringify(members));
            g_form.setValue('v_chefs', JSON.stringify(members));
        }

    });	

}
