sys_script_include.script:
var VariableGlideRecords = Class.create();
VariableGlideRecords.prototype = {
    initialize: function() {},

    getVarGlideRecordField: function(tablename, recsysid, fieldname) {

        let tblName = tablename;
        let recSysID = recsysid;
        let fldName = fieldname;
        let result = '';

        result = 'AS0945SysID';

        /*
        let query = fldName + 'ISNOTEMPTY^sys_id=' + recSysID;
        // 'business_service.owned_byISNOTEMPTY^sys_id=424cdcec83a0d2103a7b97a6feaad348'

        //gs.addInfoMessage('Table: ' + tblName);
        //gs.addInfoMessage('Query: ' + query);

        var neededGR = new GlideRecord(tblName);
        neededGR.addEncodedQuery(query);
        neededGR.query();

        if (neededGR.next()) {
        	//gs.addInfoMessage('Next(SysID): ' + neededGR.sys_id);
        	gs.addInfoMessage('Next(business Service): ' + neededGR.getValue('business_service.owned_by'));
        	result = neededGR.business_service.owned_by;
        }
        */

        gs.addInfoMessage('Table: ' + tblName);


        //var fieldPath = "opened_by.department.name"; // Beispiel Dot-Walk-String
        var fieldPath = fldName;
        var fieldParts = fieldPath.split('.'); // Aufteilen des Strings

        var gr = new GlideRecord(tblName);
        if (gr.get('sys_id', recSysID)) {
            var currentGr = gr; // Start mit der Incident-Tabelle

            for (var i = 0; i < fieldParts.length - 1; i++) {
                var field = fieldParts[i];

                // Wenn es ein Referenzfeld ist (z.B. 'opened_by'), mache eine neue GlideRecord-Abfrage
                if (currentGr.isValidField(field) && currentGr.getValue(field)) {
                    var refSysId = currentGr.getValue(field); // sys_id des referenzierten Datensatzes
                    currentGr = new GlideRecord(currentGr.getElement(field).getReferenceTable());
                    currentGr.get(refSysId); // Lade den referenzierten Datensatz
                } else {
                    gs.error('Invalid field or reference: ' + field);
                    break;
                }
            }

            // Letztes Feld abrufen (z.B. 'name' in diesem Fall)
            var finalField = fieldParts[fieldParts.length - 1];
            if (currentGr.isValidField(finalField)) {
                var finalValue = currentGr.getValue(finalField);
                gs.info('Final value: ' + finalValue);
            } else {
                gs.error('Invalid field: ' + finalField);
            }
        }

		result = finalValue;

        return result;

    },

    type: 'VariableGlideRecords'
};
