sys_script_include.script:
var AjaxServiceCatalogUtils = Class.create();
AjaxServiceCatalogUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    getGroupMembers: function() {

        var groupSysID = this.getParameter('sysparm_groupSysID');
        gs.info('[AjaxServiceCatalogUtils][getGroupMembers] Start SCI!' + groupSysID);

        var members = [];

        var memberGR = new GlideRecord('sys_user_grmember');
        memberGR.addQuery('group', groupSysID);
        //memberGR.addNullQuery('u_function');  // members haben hier die sys_id stehen
        memberGR.addEncodedQuery('u_function!=Kassenwart^ORu_function=NULL^u_function!=Vorsitzender^ORu_function=NULL^u_function!=Teamsprecher^ORu_function=NULL');
        memberGR.query();

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] after query SCI!');

        while (memberGR.next()) {
            members.push(memberGR.user.toString());
            //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] user: ' + memberGR.user.toString());

        }

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] Ende SCI!' + members);

        return JSON.stringify(members);

    },

    getChefsfromTeam: function() {

        var teamSysID = this.getParameter('sysparm_groupSysID');
        chefsArr = [];

        gs.info('[AjaxServiceCatalogUtils][getChefsfromTeam] Start SCI!' + teamSysID);

        var theChefs = new ChefDataObjectLoader();
        var theChefsArr = theChefs.getChefsArray(teamSysID);

        gs.info('[AjaxServiceCatalogUtils][getChefsfromTeam] before for!' + theChefsArr.length + ' | ' + theChefsArr[0]);

        for (var i = 0; i < theChefsArr.length; i++) {

            var theChef = new GlideRecord('sys_user');
            theChef.get(theChefsArr[i]);

            gs.info('[AjaxServiceCatalogUtils][getChefsfromTeam] before for!' + theChefsArr.length + ' | ' + theChef.last_name);

            if (theChef) {
                var myChefObj = new ChefDataObjectLoader();
                var myChef = myChefObj.createOneChef(theChef.email, theChef.first_name, theChef.last_name, theChef.sys_id);
                gs.info('[AjaxServiceCatalogUtils][getChefsfromTeam] in for!' + chefsArr.length + ' | ' + JSON.stringify(myChef));
                chefsArr.push(myChef);

            }

            gs.info('[AjaxServiceCatalogUtils][getChefsfromTeam] after for!' + chefsArr.length + ' | ' + JSON.stringify(chefsArr));

        }

        return JSON.stringify(chefsArr);

    },

    type: 'AjaxServiceCatalogUtils'
});
