var ChefDataObjectLoader = Class.create();
ChefDataObjectLoader.prototype = Object.extendsObject(ConfigLoader, {
    //DemandDataObjectLoader.prototype = {

    //initialize: function() {},

    initialize: function() {
        ConfigLoader.prototype.initialize.call(this);
    },

    createOneChef: function(Email, Firstname, Lastname, SID) {

        gs.info('[ChefDataObjectLoader][createOneChef] Start: ' + Email + '|' + Firstname + '|' + Lastname + '|' + SID);

        var oneChefObj = new ChefDataObject();
        //oneChefObj.initialize();
        //oneChefObj["w_email"] = Email;

        gs.info('[ChefDataObjectLoader][createOneChef] Start: ' + oneChefObj.toString());

        oneChefObj.w_email = Email.toString();
        oneChefObj.w_vorname = Firstname.toString();
        oneChefObj.w_nachname = Lastname.toString();
        oneChefObj.w_vorsitzender = SID.toString();

        gs.info('[ChefDataObjectLoader][createOneChef] End: ' + oneChefObj.toString());

        return oneChefObj;
    },

    getChefsArray: function(teamSysID) {
        var members = [];

        var memberGR1 = new GlideRecord('sys_user_grmember');
        memberGR1.addQuery('group', teamSysID);
        //memberGR.addNullQuery('u_function');  // members haben hier die sys_id stehen
        memberGR1.addEncodedQuery('u_function=Vorsitzender');
        memberGR1.query();

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] after query SCI!');

        if (memberGR1.next()) {
            members.push(memberGR1.user.toString());
            //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] user: ' + memberGR.user.toString());

        }

        var memberGR2 = new GlideRecord('sys_user_grmember');
        memberGR2.addQuery('group', teamSysID);
        //memberGR.addNullQuery('u_function');  // members haben hier die sys_id stehen
        memberGR2.addEncodedQuery('u_function=Kassenwart');
        memberGR2.query();

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] after query SCI!');

        if (memberGR2.next()) {
            members.push(memberGR2.user.toString());
            //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] user: ' + memberGR.user.toString());

        }

        var memberGR3 = new GlideRecord('sys_user_grmember');
        memberGR3.addQuery('group', teamSysID);
        //memberGR.addNullQuery('u_function');  // members haben hier die sys_id stehen
        memberGR3.addEncodedQuery('u_function=Teamsprecher');
        memberGR3.query();

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] after query SCI!');

        if (memberGR3.next()) {
            members.push(memberGR3.user.toString());
            //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] user: ' + memberGR.user.toString());

        }

        //gs.info('[AjaxServiceCatalogUtils][getGroupMembers] Ende SCI!' + members);

        return members;
    },

    type: 'ChefDataObjectLoader'
});
