sys_script_include.script:
var URLBuilder = Class.create();
URLBuilder.prototype = {

    initialize: function () {},

	buildAddOnValueURL: function (sysId) {
		var url = '';
		var query = QueryBuilder.findAddOnValue(sysId);

		//gs.debug('[My Custom App][buildAddOnValueURL][URLBuilder] query ==> ' + query);

		var record = GlideUtils.getGlideRecord(Constants.ADDON_VALUES_TABLE, query);

		//gs.debug('[My Custom App][buildAddOnValueURL][URLBuilder] record ==> ' + record);

		if(record.next()) {
			url = Utils.buildPageUrl(Constants.ADDON_VALUES_TABLE, record.getValue('sys_id'));
		}

		gs.info('[My Custom App][buildAddOnValueURL][URLBuilder] url ==> ' + url);

		return url;
	},

    type: 'URLBuilder'
};
