var StringBuilder = Class.create();
StringBuilder.prototype = {
  initialize: function (string) {
    this.resultString = [];

    this.string = string;
  },

  append: function (string) {
    this.resultString.push(string);

    return this;
  },

  replaceAt: function (index, character) {
    this.resultString.push(this.string.substr(0, index) + character + this.string.substr(index + character.length));

    return this;
  },

  deleteCharAt: function (index) {
    index = index - 1;

    if (index >= 0) {
      var array = this.string.split('');
      array.splice(index, 1);
      array = array.join('');

      this.resultString.push(array);
    } else {
      this.resultString.push(this.string);
    }

    return this;
  },

  insert: function (offset, insert) {
    if (offset > 0) {
      this.resultString.push(this.string.substring(0, offset) + insert + this.string.substring(offset, this.length));
    } else {
      this.resultString.push(insert + this.string);
    }

    return this;
  },

  toString: function () {
    var result = this.resultString.join('');

    // eslint-disable-next-line no-new-wrappers
    return new String(result);
  },

  type: 'StringBuilder'
};
