sys_script_include.script:
GlideUtils = {

  getRecord: function (table, column, identifier) {
    var record = null;
    var gr = new GlideRecord(table);
    if (gr.get(column, identifier)) {
      record = gr;
    }

    return record;
  },

  getGlideRecord: function (table, query) {
    var record = new GlideRecord(table);
    record.addEncodedQuery(query);
    record.query();

    return record;
  },

  getAllGlideRecord: function (table) {
    var record = new GlideRecord(table);
    record.query();

    return record;
  },

  getLimitedGlideRecord: function (table, query, limit) {
    var record = new GlideRecord(table);
    record.addEncodedQuery(query);
    record.setLimit(limit);
    record.query();

    return record;
  }

};
