var ConfiguratorAjaxLoader = Class.create();
ConfiguratorAjaxLoader.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

    //   initialize: function (request, responseXML, gc) {
    //     global.AbstractAjaxProcessor.prototype.initialize.call(this, request, responseXML, gc);
    //   },

    loadAddOnValueURL: function() {
        var sysId = this.getParameter('sysparm_sys_id');
        var urlBuilder = new URLBuilder();
        var url = urlBuilder.buildAddOnValueURL(sysId);

        gs.info("My Custom App - ConfiguratorAjaxLoader - url =====> " + url);
        //gs.log("My Custom App - ConfiguratorAjaxLoader - sysId =====> ", "My Custom App");
        //jslog("This message is from My Custom App - ConfiguratorAjaxLoader jslog().");

        return url;
    },

    getNumOfAddonEntries: function() {
        var num = 2;

        var sysId = this.getParameter('sysparm_sys_id');
        gs.addInfoMessage("My Custom App - ConfiguratorAjaxLoader - getNumOfAddonEntries - sys_id: " + sysId);

        //var addonGa = new GlideRecord('sys_user_grmember');
		var grpMems = new GlideRecord('x_653072_my_custom_my_addon_table');
        grpMems.addQuery('number.sys_id', sysId);
        //addonGA.addAggregate('COUNT');
        grpMems.query();

        //number.sys_id= d0d3c5491b31301010df31d7dc4bcb3f 
        //if (addonGA.next()) {
        num = grpMems.getRowCount();
        gs.addInfoMessage("My Custom App - ConfiguratorAjaxLoader - getNumOfAddonEntries - num: " + num);
        return num;
        //}
    },

    countGroupMembers: function() {

        var grpName = "";
        var message = "There are no members in this Assignment Group";
		gs.addInfoMessage(message + " 1 countGroupMembers");
        var groupID = this.getParameter('sysparm_group_id');

        var grpMems = new GlideRecord('sys_user_grmember');
        grpMems.addQuery('group.sys_id', groupID);
		gs.addInfoMessage(message + " 2 countGroupMembers groupID: " + groupID);
        grpMems.query();

        if (grpMems.next()) {
            grpName = grpMems.getDisplayValue('group');
			gs.addInfoMessage(message + " 3a countGroupMembers groupName: " + grpName);
        }
        if (grpName != "") {
            message = "There are " + grpMems.getRowCount() + " members in the " + grpName + " group";
			gs.addInfoMessage(message + " 3b countGroupMembers groupID: " + groupID);
        }

        return message;
    },

    type: 'ConfiguratorAjaxLoader'
});
