sys_ui_action.script:
function openAddOnValues() {
  var uniqueValue = g_form.getUniqueValue();

  jslog("My Custom App - My Addon Button - This message is from jslog().");
  jslog('My Custom App - My Addon Button - SysId: = ' + uniqueValue);
  jslog("My Custom App - My Addon Button - The currently logged in user is " + g_user.getFullName() + ".");

  //gs.debug("My Custom App - My Addon Button - sysId =====> ");

  var addOnValueURL = new GlideAjax('ConfiguratorAjaxLoader');
  addOnValueURL.addParam('sysparm_name', 'loadAddOnValueURL');
  addOnValueURL.addParam('sysparm_sys_id', uniqueValue);

  jslog('My Custom App - My Addon Button - URL: ===> vor getXML');

  //addOnValueURL.getXML(response);
  try{
	     addOnValueURL.getXML(response);
    } catch (err) {
         gs.error("My Custom App - My Addons UI Action: a JavaScript runtime error occurred - " + err);
	}
  jslog('My Custom App - My Addon Button - URL: ===> nach getXML');

  function response (response) {
    var answer = response.responseXML.documentElement.getAttribute('answer');

    if (answer) {
	  jslog('My Custom App - My Addon Button - URL: ===> ' + answer);
	  g_form.setValue('u_string_short_desc','Hallo -> ' + answer);
      //window.open(answer, '_blank');
	  g_navigation.openPopup(answer);
    } else {
	  jslog('My Custom App - My Addon Button - URL existiert nicht');
	}
  }
}
